con:config.script:
String projectId = ''
String issueId = ''

assert context.response, 'Response is empty or null'
def json = new groovy.json.JsonSlurper().parseText(context.response)
log.info json[0].id
projectId = json[0].id.toString()

def issueTypes = json[0].issueTypes

for(def issueType : issueTypes) {
      if(issueType.name == 'Story') {
      issueId = issueType.id
      break
      }
}

log.info issueId

context.testCase.testSuite.setPropertyValue('projectId', projectId)
context.testCase.testSuite.setPropertyValue('issueId', issueId)

def generator = { String alphabet, int n ->
  new Random().with {
    (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join()
  }
}

String rString = generator( (('A'..'Z')+('0'..'9')).join(), 5 )

context.testCase.testSuite.setPropertyValue('SProjectName', 'DORA Software Test '+rString)
context.testCase.testSuite.setPropertyValue('SProjectKey', 'DORS'+rString)
context.testCase.testSuite.setPropertyValue('BProjectName', 'DORA Business Test '+rString)
context.testCase.testSuite.setPropertyValue('BProjectKey', 'DORB'+rString)

